// listen for resize changes
function detectOrientation() {
  if (window.innerHeight > window.innerWidth) {
    svgarea.style.width = "100%";
  } else {
    svgarea.style.width = "50%";
  }
}
window.addEventListener("resize", function() {
  detectOrientation();
}, false);
detectOrientation();

// generate a random number
function randomNumber(n) {
  return Math.floor( Math.random() * n );
}

// randomize Chibi characters
function randomizeChibi() {
  // Head Area
  // apply hair
  var ranHair = randomNumber(characterAttributes.hair.length);
  ranHair     = characterAttributes.hair[ranHair];
  hair.innerHTML = ranHair;
  
  // apply eyes
  var ranEyes = randomNumber(characterAttributes.eyes.length);
  ranEyes     = characterAttributes.eyes[ranEyes];
  eyes.innerHTML = ranEyes;
  
  // apply mouth
  var ranMouth = randomNumber(characterAttributes.mouth.length);
  ranMouth     = characterAttributes.mouth[ranMouth];
  mouth.innerHTML = ranMouth;
  
  // apply ears
  var ranEars = randomNumber(characterAttributes.ears.length);
  ranEars     = characterAttributes.ears[ranEars];
  ears.innerHTML = ranEars;
  
  // apply noses
  var ranNose = randomNumber(characterAttributes.nose.length);
  ranNose     = characterAttributes.nose[ranNose];
  nose.innerHTML = ranNose;
  
  // apply misc (blushes, scractes, etc:)
  var ranMisc = randomNumber(characterAttributes.misc.length);
  ranMisc     = characterAttributes.misc[ranMisc];
  misc.innerHTML = ranMisc;
  
  // Body Area
  // apply random apparel
  var ranApparel = randomNumber(characterAttributes.apparel.length);
  ranApparel     = characterAttributes.apparel[ranApparel];
  apparel.innerHTML = ranApparel;
  
  // apply random apparel
  var ranMiscbody = randomNumber(characterAttributes.miscbody.length);
  ranMiscbody     = characterAttributes.miscbody[ranMiscbody];
  miscbody.innerHTML = ranMiscbody;
}
randomizeChibi();

// download Chibi character
function downloadChibi() {
  swal({
    title: 'File name below!',
    input: 'text',
    inputPlaceholder: ".svg is added on save",
    showCancelButton: true,
    confirmButtonText: 'Save',
    showLoaderOnConfirm: true
  }).then((result) => {
    if (result) {
      var blob = new Blob([ svgarea.innerHTML ], {type: "text/html"});
      saveAs(blob, result + ".svg");

      swal(
        'Yay!',
        'You\'re character was successfully saved!',
        'success'
      );
    } else {
      swal(
        'Oops!',
        console.error(result),
        'error'
      );
    }
  });
};

// download final image as png
function snapshot() {
//  // scroll to top
//  window.scrollTo({ top: 0 });
  
  // show white background
  whitefill.style.display = "block";
  
  // grab main author text
  grabDesign.classList.add("screenshot", "static");
  var mainAuthorTxt = author.innerHTML;
  author.innerHTML = "<h2 class=\"whitetxt nomar\">https://michaelsboost.com/chibigenerator/</h2>";

  // hide header & buttons
  btnArea.classList.add("hide");
  header.classList.add("hide");
  
  // set canvas width to a fixed size
  grabDesign.style.width = "1000px";
  grabDesign.style.height = "600px";
  grabDesign.style.background = "#4A98E1";
  
  var tempEl = document.createElement("div")
  tempEl.id = tempEl
  fileName = "chibi-character";
  
  // convert website to image
  html2canvas(grabDesign, {
    allowTaint: true,
    useCORS: true,
    foreignObjectRendering: true
  }).then(function(canvas) {
    // download canvas image
    myBase64 = canvas.toDataURL("image/png");
    var iOS = !!navigator.platform && /iPad|iPhone|iPod/.test(navigator.platform);
    if (iOS === true) {
      // remove image if already visible
      var img = new Image();
      img.crossOrigin = "Anonymous";
      img.id = "getshot";
      img.src = myBase64;
      document.body.appendChild(img);

      var a = document.createElement("a");
      a.href = getshot.src;
      a.download = fileName + ".png";
      a.click();
      document.body.removeChild(img);
    } else {
      canvas.toBlob(function(blob) {
        saveAs(blob, fileName + ".png");
      }, "image/png");
    }
  });

  // revert canvas styles back to defaults
  grabDesign.style.width = "";
  grabDesign.style.height = "";
  grabDesign.style.background = "";
  
  // show header & buttons
  header.classList.remove("hide");
  btnArea.classList.remove("hide");
  
  // reapply main author text
  author.innerHTML = mainAuthorTxt;
  grabDesign.classList.remove("screenshot", "static");

  // fade out white background
  $(".whitefill").fadeOut();
};

// randomize character button
randomize.onclick = function() {
  randomizeChibi();
};